sqlReaderQuery.value:
select TABLE_NAME, COLUMN_NAME, DATA_TYPE, CHARACTER_MAXIMUM_LENGTH as CHAR_DATA_LENGTH,
NUMERIC_PRECISION, NUMERIC_SCALE, DATETIME_PRECISION, CHARACTER_MAXIMUM_LENGTH as CLOB_DATA_LENGTH, ORDINAL_POSITION as COLUMN_POSITION, IS_NULLABLE
from INFORMATION_SCHEMA.COLUMNS
where TABLE_SCHEMA = '@{pipeline().parameters.schemaName}' and @{pipeline().parameters.tableNameSelector}
order by TABLE_NAME;

query.value:
select table_name,column_name, data_type_name as DATA_TYPE, LENGTH as CHAR_DATA_LENGTH, position as COLUMN_POSITION 
from table_columns where schema_name = '@{pipeline().parameters.schemaName}' and @{pipeline().parameters.tableNameSelector}
ORDER BY TABLE_NAME;

